mapper.select:
SELECT *
FROM account
WHERE account_id = #{account_id} and account_phone = #{account_phone}

mapper.update:
UPDATE account
SET account_main = 0
WHERE account_id = #{account_id} and account_num = #{account_phone}

UPDATE account
SET account_main = 1
WHERE account_id = #{account_id} and account_num = #{account_phone}

mapper.select:
SELECT group_account
FROM groups
WHERE account_num= #{account_num}

mapper.update:
UPDATE account
SET account_use = 1
WHERE account_num = #{account_num}
